# Complete Financial Market Forecaster GitHub Pages Setup
# Created: 2025-06-06
# Author: GitHub Copilot for asarekings

# Start by removing any existing content (optional)
Remove-Item -Path * -Recurse -Force -Exclude *.git, .gitignore -ErrorAction SilentlyContinue

# Create .nojekyll file to bypass Jekyll processing
New-Item -Path ".nojekyll" -ItemType File -Force | Out-Null
Write-Output "Created .nojekyll file"

# Create needed directories
New-Item -Path "css" -ItemType Directory -Force | Out-Null
New-Item -Path "js" -ItemType Directory -Force | Out-Null
New-Item -Path "img" -ItemType Directory -Force | Out-Null
Write-Output "Created directories"

# Create CSS file
$cssContent = "body {`n  font-family: -apple-system, BlinkMacSystemFont, `"Segoe UI`", Roboto, Helvetica, Arial, sans-serif;`n  line-height: 1.6;`n  color: #333;`n  background-color: #f8f9fa;`n}`n`n.navbar-brand {`n  font-weight: bold;`n}`n`n.feature-card {`n  transition: transform 0.3s ease;`n  margin-bottom: 20px;`n  height: 100%;`n}`n`n.feature-card:hover {`n  transform: translateY(-5px);`n  box-shadow: 0 10px 20px rgba(0,0,0,0.1);`n}`n`n.dashboard-welcome {`n  margin-bottom: 2rem;`n}`n`n.card {`n  margin-bottom: 1.5rem;`n  box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075);`n}`n`n.card-header {`n  font-weight: 600;`n  background-color: rgba(0, 0, 0, 0.03);`n}`n`n/* Charts */`ncanvas {`n  width: 100% !important;`n  height: 250px !important;`n}`n`n#historicalPerformanceChart,`n#forecastPrediction {`n  height: 300px;`n  width: 100%;`n}`n`n/* Sentiment score */`n.sentiment-score {`n  font-size: 2.5rem;`n  font-weight: bold;`n  text-align: center;`n  margin: 20px 0;`n}`n`n.sentiment-positive {`n  color: #198754;`n}`n`n.sentiment-neutral {`n  color: #6c757d;`n}`n`n.sentiment-negative {`n  color: #dc3545;`n}`n`n/* Footer */`nfooter {`n  margin-top: 4rem;`n  padding-top: 1.5rem;`n  padding-bottom: 1.5rem;`n  border-top: 1px solid #e9ecef;`n  color: #6c757d;`n}"
Set-Content -Path "css/style.css" -Value $cssContent
Write-Output "Created css/style.css"

# Create main.js
$mainJsContent = "document.addEventListener('DOMContentLoaded', function() {`n  // Initialize tooltips if using Bootstrap`n  var tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle=`"tooltip`"]'))`n  var tooltipList = tooltipTriggerList.map(function (tooltipTriggerEl) {`n    return new bootstrap.Tooltip(tooltipTriggerEl)`n  });`n  `n  // Update the current year in the footer copyright`n  document.getElementById('current-year').textContent = new Date().getFullYear();`n});"
Set-Content -Path "js/main.js" -Value $mainJsContent
Write-Output "Created js/main.js"

# Create dashboard.js (shortened for brevity)
$dashboardJsContent = "document.addEventListener('DOMContentLoaded', function() {`n  // Initialize all charts when document is ready`n  initializeMarketOverview();`n  initializeSentimentGauge();`n  initializeForecastPrediction();`n  initializeHistoricalPerformance();`n  updateIndicators();`n  updateNewsList();`n  updateMacroIndicators();`n  `n  // Set last update time`n  document.getElementById('marketUpdateTime').textContent = formatDate(new Date());`n  document.getElementById('sentimentSourceCount').textContent = '1,247';`n  document.getElementById('predictionConfidence').textContent = '87%';`n});`n`n// Format date for display`nfunction formatDate(date) {`n  return new Date(date).toLocaleString('en-US', {`n    year: 'numeric',`n    month: 'short',`n    day: 'numeric',`n    hour: '2-digit',`n    minute: '2-digit'`n  });`n}`n`n// Generate random data for demo purposes`nfunction generateRandomData(count, min, max) {`n  const data = [];`n  for (let i = 0; i < count; i++) {`n    data.push(Math.random() * (max - min) + min);`n  }`n  return data;`n}`n`n// Generate date labels for charts`nfunction generateDateLabels(count) {`n  const labels = [];`n  const today = new Date();`n  `n  for (let i = count - 1; i >= 0; i--) {`n    const date = new Date();`n    date.setDate(today.getDate() - i);`n    labels.push(date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }));`n  }`n  `n  return labels;`n}`n`n// Market Overview Chart`nfunction initializeMarketOverview() {`n  const ctx = document.getElementById('marketOverviewChart').getContext('2d');`n  `n  // Sample data for S&P 500, NASDAQ, and Dow Jones`n  const labels = generateDateLabels(30);`n  const sp500Data = generateRandomData(30, 4500, 4700);`n  const nasdaqData = generateRandomData(30, 14000, 14500);`n  const dowData = generateRandomData(30, 35000, 36000);`n  `n  const chart = new Chart(ctx, {`n    type: 'line',`n    data: {`n      labels: labels,`n      datasets: [`n        {`n          label: 'S&P 500',`n          data: sp500Data,`n          borderColor: 'rgba(75, 192, 192, 1)',`n          backgroundColor: 'rgba(75, 192, 192, 0.2)',`n          tension: 0.4`n        },`n        {`n          label: 'NASDAQ',`n          data: nasdaqData,`n          borderColor: 'rgba(153, 102, 255, 1)',`n          backgroundColor: 'rgba(153, 102, 255, 0.2)',`n          tension: 0.4`n        },`n        {`n          label: 'Dow Jones',`n          data: dowData,`n          borderColor: 'rgba(255, 159, 64, 1)',`n          backgroundColor: 'rgba(255, 159, 64, 0.2)',`n          tension: 0.4`n        }`n      ]`n    },`n    options: {`n      responsive: true,`n      plugins: {`n        legend: {`n          position: 'top',`n        },`n        tooltip: {`n          mode: 'index',`n          intersect: false`n        }`n      },`n      scales: {`n        y: {`n          beginAtZero: false`n        }`n      }`n    }`n  });`n}`n`n// Sentiment Gauge Chart`nfunction initializeSentimentGauge() {`n  const ctx = document.getElementById('sentimentGaugeChart').getContext('2d');`n  `n  // Sample sentiment score (range: -1 to 1)`n  const sentimentScore = 0.65;`n  `n  const chart = new Chart(ctx, {`n    type: 'doughnut',`n    data: {`n      labels: ['Positive', 'Negative'],`n      datasets: [{`n        data: [sentimentScore + 1, 2 - (sentimentScore + 1)],`n        backgroundColor: [`n          'rgba(75, 192, 192, 0.8)',`n          'rgba(255, 99, 132, 0.8)'`n        ],`n        borderColor: [`n          'rgba(75, 192, 192, 1)',`n          'rgba(255, 99, 132, 1)'`n        ],`n        borderWidth: 1`n      }]`n    },`n    options: {`n      responsive: true,`n      circumference: 180,`n      rotation: 270,`n      plugins: {`n        legend: {`n          display: false`n        },`n        tooltip: {`n          callbacks: {`n            label: function(context) {`n              return 'Sentiment: ' + ((sentimentScore * 100).toFixed(1) + '%');`n            }`n          }`n        }`n      }`n    }`n  });`n}`n`n// Placeholder functions to prevent errors`nfunction initializeForecastPrediction() {`n  console.log('Forecast prediction initialized');`n}`n`nfunction initializeHistoricalPerformance() {`n  console.log('Historical performance initialized');`n}`n`nfunction updateIndicators() {`n  document.getElementById('rsiValue').textContent = '62.5 (Neutral)';`n  document.getElementById('macdValue').textContent = '1.2 (Bullish)';`n  document.getElementById('bollingerValue').textContent = 'Middle Band (Neutral)';`n  document.getElementById('ma50Value').textContent = '4,632.54 (Above Price)';`n  document.getElementById('ma200Value').textContent = '4,487.22 (Below Price)';`n}`n`nfunction updateNewsList() {`n  console.log('News list updated');`n}`n`nfunction updateMacroIndicators() {`n  console.log('Macro indicators updated');`n}"
Set-Content -Path "js/dashboard.js" -Value $dashboardJsContent
Write-Output "Created js/dashboard.js"

